mapper.select:
SELECT
-- 级联表的字段 ,yiyuan.yiyuan_name as yiyuanName ,yiyuan.yiyuan_types as yiyuanTypes ,yiyuan.yiyuan_photo as yiyuanPhoto ,yiyuan.yiyuan_address as yiyuanAddress ,yiyuan.yiyuan_content as yiyuanContent
FROM yimiao a left JOIN yiyuan yiyuan ON a.yiyuan_id = yiyuan.id
and a.id in
#{item}
and a.yimiao_name like CONCAT('%',#{params.yimiaoName},'%')
and a.yimiao_types = #{params.yimiaoTypes}
and ( a.yiyuan_id = #{params.yiyuanId} )
and a.yimiao_clicknum >= #{params.yimiaoClicknumStart}
and a.yimiao_clicknum <= #{params.yimiaoClicknumEnd}
and a.yimiao_clicknum = #{params.yimiaoClicknum}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yimiao_content like CONCAT('%',#{params.yimiaoContent},'%')
and a.yiyuan_id IS NOT NULL
and yiyuan.yiyuan_name like CONCAT('%',#{params.yiyuanName},'%')
and yiyuan.yiyuan_types = #{params.yiyuanTypes}
and yiyuan.yiyuan_address like CONCAT('%',#{params.yiyuanAddress},'%')
and yiyuan.yiyuan_content like CONCAT('%',#{params.yiyuanContent},'%')
order by a.${params.orderBy} desc
