mapper.select:
SELECT
-- 级联表的字段 ,yimiao.yimiao_name as yimiaoName ,yimiao.yimiao_types as yimiaoTypes ,yimiao.yimiao_photo as yimiaoPhoto ,yimiao.yimiao_clicknum as yimiaoClicknum ,yimiao.yimiao_content as yimiaoContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_delete as yonghuDelete
FROM yuyuejiezong a left JOIN yimiao yimiao ON a.yimiao_id = yimiao.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.yonghu_id = #{params.yonghuId} )
and ( a.yimiao_id = #{params.yimiaoId} )
and UNIX_TIMESTAMP(a.yuyuejiezong_time) >= UNIX_TIMESTAMP(#{params.yuyuejiezongTimeStart})
and UNIX_TIMESTAMP(a.yuyuejiezong_time) <= UNIX_TIMESTAMP(#{params.yuyuejiezongTimeEnd})
and a.yuyuejiezong_text like CONCAT('%',#{params.yuyuejiezongText},'%')
and a.yuyuejiezong_yesno_types = #{params.yuyuejiezongYesnoTypes}
and a.yuyuejiezong_yesno_text like CONCAT('%',#{params.yuyuejiezongYesnoText},'%')
and a.yimiao_id IS NOT NULL
and yimiao.yimiao_name like CONCAT('%',#{params.yimiaoName},'%')
and yimiao.yimiao_types = #{params.yimiaoTypes}
and yimiao.yiyuan_id = #{params.yiyuanId}
and yimiao.yimiao_clicknum >= #{params.yimiaoClicknumStart}
and yimiao.yimiao_clicknum <= #{params.yimiaoClicknumEnd}
and yimiao.yimiao_clicknum = #{params.yimiaoClicknum}
and UNIX_TIMESTAMP(yimiao.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(yimiao.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and yimiao.yimiao_content like CONCAT('%',#{params.yimiaoContent},'%')
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
